package com.marsanvi.crypto;import org.springframework.stereotype.Service;@Servicepublic class CaesarService {	public String encode(String text, int offset){		offset = offset % 26 + 26;        StringBuilder encoded = new StringBuilder();        text = text.toLowerCase();        for (char i : text.toCharArray()) {            if (Character.isLetter(i)) {                    encoded.append((char) ('a' + (i - 'a' + offset) % 26 ));            } else {                encoded.append(i);            }        }        return encoded.toString();	}	public String decode(String text, int offset) {		return encode(text,26-offset);	}}