package com.marsanvi.crypto;import org.springframework.stereotype.Service;@Servicepublic class CaesarService {		private String alphabet;	public CaesarService(String alphabet) {		this.alphabet = alphabet;	}		public String encode(String text, int offset) {		offset = offset % alphabet.length() + alphabet.length();		StringBuilder encoded = new StringBuilder();		text = text.toLowerCase();		for (char i : text.toCharArray()) {			if (Character.isLetter(i)) {				encoded.append(alphabet.charAt(( alphabet.indexOf(i) + offset) % alphabet.length()));			} else {				encoded.append(i);			}		}		return encoded.toString();	}	public String decode(String text, int offset) {		return encode(text, 26 - offset);	}	}