package com.marsanvi.crypto;import org.springframework.stereotype.Service;@Servicepublic class SubstitutionService {	public String encode(String alphabet,String cipher, String text) {		StringBuilder encodedSb = new StringBuilder();		for (char i : text.toCharArray()) {			encodedSb.append(cipher.charAt(alphabet.indexOf(i)));		}		return encodedSb.toString();	}	public String decode(String alphabet,String cipher, String text) {		return encode(cipher,alphabet,text);	}}