package com.marsanvi.crypto.services;import org.springframework.stereotype.Service;@Servicepublic class CaesarService {	public String encode(String alphabet, String text, int offset) {		offset = offset % alphabet.length() + alphabet.length();		StringBuilder encodedSb = new StringBuilder();		text = text.toLowerCase();		for (char i : text.toCharArray()) {			if (Character.isLetter(i)) {				encodedSb.append(alphabet.charAt((alphabet.indexOf(i) + offset) % alphabet.length()));			} else {				encodedSb.append(i);			}		}		return encodedSb.toString();	}	public String decode(String alphabet, String text, int offset) {		return encode(alphabet, text, alphabet.length() - offset);	}}